mapper.select:
SELECT
fun_module_id, sort_code, name, func_url, parent_id, code, is_disabled,
creator_id, create_time, sort, is_del, channel
FROM
tb_admin_function

	is_del = '0'

		AND name = #{name,jdbcType=VARCHAR}


		AND parent_id = #{parentId,jdbcType=BIGINT}


		AND fun_module_id != #{funModuleId,jdbcType=BIGINT}

	LIMIT 1

SELECT ( CASE WHEN t.fun_module_id IS NULL THEN 0 ELSE 1 END ) AS selected, af.fun_module_id, af.parent_id, af.sort, af.sort_code, af.name, af.code, af.func_url, af.is_disabled, af.is_del, af.creator_id, af.create_time, af.channel FROM tb_admin_function af LEFT JOIN ( SELECT f.fun_module_id FROM tb_admin_role_fun rf LEFT JOIN tb_admin_function f ON rf.fun_module_code LIKE CONCAT(f.sort_code, '%' )
f.is_del = '0' AND f.is_disabled = '0'
AND f.parent_id = #{parentId,jdbcType=BIGINT}
AND rf.role_id = #{roleId,jdbcType=BIGINT}
GROUP BY f.fun_module_idis_del ) t ON af.fun_module_id = t.fun_module_id
af.is_del = '0' AND af.is_disabled = '0'
AND af.parent_id = #{parentId,jdbcType=BIGINT}
ORDER BY af.sort

SELECT DISTINCT f.fun_module_id, f.parent_id, f.sort, f.sort_code, f.name, f.code, f.func_url, f.is_disabled, f.is_del, f.creator_id, f.create_time, f.channel FROM tb_admin_role_fun rf LEFT JOIN tb_admin_function f ON rf.fun_module_code LIKE CONCAT(f.sort_code, '%' )
f.is_del = '0' AND f.is_disabled = '0'
AND f.parent_id = #{parentId,jdbcType=BIGINT}
AND rf.role_id = #{roleId,jdbcType=BIGINT}
ORDER BY f.sort

SELECT
fun_module_id, sort_code, name, func_url, parent_id, code, is_disabled,
creator_id, create_time, sort, is_del, channel
FROM
tb_admin_function


		AND parent_id = #{parentId,jdbcType=BIGINT}


		AND sort_code = #{sortCode,jdbcType=VARCHAR}


		AND func_url = #{funcUrl,jdbcType=VARCHAR}


		AND is_disabled = #{isDisabled,jdbcType=VARCHAR}

	AND is_del='0'

ORDER BY sort
